mapper.insert:
		MERGE INTO TBL_AD_CATE_SEASON /* setSeasonCategory */
		    USING DUAL
		    ON (SITE_NO = #{siteNo}
		    AND SEASON_GUBUN = #{seasonGubun})
		    
		WHEN MATCHED THEN
		
			UPDATE SET
				SEASON_CD = #{seasonCd}
        		,SEASON_CD_DISP = #{seasonCdDisp}
        		
					
						,DISP_YN = #{dispYn}
					
					
						 ,DISP_YN = 'N'
					
				
				,UPDR_NO = #{updrNo}
				,UPD_DTS = SYSDATE
				
					
						,TOP_YN = #{topYn}
					
					
						,TOP_YN = 'N'
					
				
		
		WHEN NOT MATCHED THEN
		
			INSERT (
					 SEASON_SEQ
					,SITE_NO
					,SEASON_CD
					,SEASON_GUBUN
					,SEASON_CD_DISP
					,SEASON_START
					,SEASON_END
					,DISP_YN
					,USE_YN
					,REGR_NO
					,REG_DTS
					,UPDR_NO
					,UPD_DTS
					,TOP_YN
					)
			VALUES(
				 SEQ_TBL_AD_CATE_SEASON.NEXTVAL
				,#{siteNo}
				,#{seasonCd}
				,#{seasonGubun}
				,#{seasonCdDisp}
				,''
				,''
				
					
						,#{dispYn}
					
					
						,'N'
					
				
				,'Y'
				,#{updrNo}
				,SYSDATE
				,#{updrNo}
				,SYSDATE
				
					
						,#{topYn}
					
					
						,'N'
					
				
			)

mapper.select:
		select /*category.listCategoryGdsCnt*/
		  main.*
		  from (
				select 
				구분 AS GUBUN
				,코드 AS CATECD
				,코드명 AS CATENM
				,구분코드 AS GUBUNCD
		   	from UBI09S_WORKS.V_코드정보
		    where 구분 = #{gubun}














		    	
		)main
		where
			(select count(*) from tbl_gd_gds gds
		      where  gds.DISP_YN = 'Y'
		      and gds.GDS_STAT = 'GD00402'
		      
		      	and SEASON_CODE = #{seasonCode}
		      
		      
		      	and SEASON_CODE != #{curSeason}

		      
		      
		      	and CATEGORY1 = MAIN.CATECD
		      
		      
		      	and CATEGORY1 = #{category1Code}
		      	and CATEGORY2 = MAIN.CATECD
		      
			  
				AND BRAND_CODE = (SELECT 코드 FROM UBI09S_WORKS.V_코드정보 WHERE 구분 = 'BRAND' AND 코드명 = (select site_desc from tbl_ad_site where site_no = #{siteNo}))
			  
              
 				AND SEX = #{sex}
			  
              
 				AND BRAND_CODE = #{brand}
			  			  
			  
		      ) > 0

SELECT CD_NM AS CATENM FROM ( SELECT MAIN.*, (select count(품번) from tbl_gd_gds gds, UBI09S_WORKS.V_상품정보트리거 ubigds where gds.ubi_gds_no = ubigds.품번 and gds.DISP_YN = 'Y' and gds.GDS_STAT = 'GD00402' and ubigds.brand코드 = MAIN.CD_NM
and ubigds.SEASON코드 = #{seasonCode}
and ubigds.SEASON코드 != #{curSeason}
)GDS_CNT FROM ( SELECT CD_NO ,SUP_CD_NO ,CD_NM ,CD_DESC ,CD_TP ,WRK_CLS_CD ,PRIR ,USE_YN ,REGR_NO ,REG_DTS ,UPDR_NO ,UPD_DTS FROM TBL_AD_CD WHERE SUP_CD_NO = 'CM013' AND USE_YN = 'Y'
AND CD_NM IN( WITH TT AS ( SELECT (SELECT CATE_DEPTH_2 FROM TBL_AD_CATE_CONFIG WHERE SEASON_SEQ =4 AND CATE_DEPTH_1='CM01503' AND USE_YN='Y') TXT FROM DUAL ) SELECT TRIM(REGEXP_SUBSTR(TXT, '[^,]+', 1, LEVEL)) AS TXT FROM TT CONNECT BY INSTR(TXT, ',', 1, LEVEL - 1) > 0 ) ORDER BY PRIR ASC )MAIN )WHERE GDS_CNT = 0

SELECT CATE_DEPTH_1 /*category.getBrand*/
	  ,CATE_DEPTH_2
FROM TBL_AD_CATE_CONFIG
WHERE CATE_DEPTH_1 = #{depthCode}
AND SEASON_SEQ = #{seasonSeq}

