SELECT * FROM DUAL

mapper.update:
UPDATE TBL_AD_CATE_CONFIG /*category.saveCate*/ SET CATE_DEPTH_2 = CASE CATE_DEPTH_1 WHEN 'CM01501' THEN
#{cateMenDepth2}
''
WHEN 'CM01502' THEN
#{cateWomenDepth2}
''
ELSE '' END ,CATE_DEPTH_3 = CASE CATE_DEPTH_1 WHEN 'CM01501' THEN
#{cateMenDepth3}
''
WHEN 'CM01502' THEN
#{cateWomenDepth3}
''
ELSE '' END ,NA_DISP_YN = CASE CATE_DEPTH_1 WHEN 'CM01501' THEN
#{menNaDispYn}
'N'
WHEN 'CM01502' THEN
#{womenNaDispYn}
'N'
ELSE '' END WHERE SEASON_SEQ = #{seasonSeq} AND CATE_DEPTH_1 != 'CM01503'

UPDATE TBL_AD_CATE_CONFIG  /*category.saveBrand*/
SET CATE_DEPTH_2 = #{brandCd}
WHERE SEASON_SEQ = #{seasonSeq}
  AND CATE_DEPTH_1 = 'CM01503'

UPDATE TBL_AD_CATE_CONFIG  /*category.saveBrand*/
