mapper.select:
SELECT C_TABLE.* FROM( SELECT ROWNUM PRNUM ,B_TABLE.* FROM( SELECT
ROW_NUMBER() OVER(ORDER BY ${orderBy} ${orderFlag}) RNUM
ROW_NUMBER() OVER(ORDER BY SORT ASC) RNUM
,A_TABLE.* FROM( SELECT TXT_NO ,TITLE ,TXT ,(SELECT CD_NM FROM TBL_AD_CD WHERE CD_NO = TXT_TP) TXT_TP ,TXT_CLS ,MOV_URL ,IMG_URL ,DECODE(DISP_YN,'Y','공개','N','비공개') DISP_YN ,READ_CNT ,PRIR ,REGR_NO ,REG_DTS ,UPDR_NO ,SLD_DISP_YN ,REPLACE(ETC1,'.','') ETC1 ,ETC2 ,THMB_URL ,PREVIEW ,ETC1 SORT ,TO_CHAR(UPD_DTS, 'YYYY.MM.DD') UPD_DTS ,(SELECT USER_NM FROM TBL_AD_USER WHERE USER_NO = CM_NEWS.UPDR_NO) REG_USER_NM FROM TBL_CM_NEWS CM_NEWS WHERE DISP_YN ='Y'
AND UPD_DTS BETWEEN to_date(#{searchStrDtm} || '000000', 'YYYY/MM/DD HH24MISS') AND to_date(#{searchEndDtm} || '235959', 'YYYY/MM/DD HH24MISS')
AND TXT_TP = #{newsType}
AND EXISTS (SELECT 'X' FROM TBL_CM_NEWS_SITE WHERE TXT_NO = CM_NEWS.TXT_NO AND SITE_NO = #{siteNo})
AND EXISTS ( SELECT 'X' FROM DUAL WHERE ('title' = #{searchGb} AND TITLE LIKE '%' || #{searchText} || '%') OR ('content' = #{searchGb} AND TXT LIKE '%' || #{searchText} || '%') OR ('all' = #{searchGb} AND (TITLE LIKE '%' || #{searchText} || '%' OR TXT LIKE '%' || #{searchText} || '%')) )
ORDER BY ${orderBy} ${orderFlag}
ORDER BY SORT DESC
) A_TABLE ORDER BY RNUM DESC ) B_TABLE )C_TABLE WHERE PRNUM BETWEEN #{startListNum} AND #{endListNum}
