mapper.update:
UPDATE TBL_AD_USER
   SET PWD 	   = #{pwd}
      ,USER_NM = #{user_name}
      ,EMAIL   = #{email}
      ,CMP_NM  = #{com_name}
      ,REPR_NM = #{repre_name}
      ,PERSON_INFO_MNGR       = #{info_name}
      ,PERSON_INFO_MNGR_EMAIL = #{info_email}
      ,ZIP_CD  = #{post_no}
      ,ADDR    = #{address}
      ,DTL_ADDR= #{dtl_address}
      ,TEL     = #{tel_no}
      ,FAX_NO  = #{fax_no}
      ,BIZ_REG_NO = #{com_no}
      ,TELE_SALES_BIZ_REG_NO = #{biz_reg_no}
      ,BIZ_ST  = #{biz_st}
      ,EVENT   = #{event}
      ,UPD_DTS = SYSDATE
 WHERE ID  = #{admin_id}

		MERGE INTO TBL_AD_SITE /*setting.siteSettingReg*/
		    USING DUAL
		    ON (SITE_NO = #{site_no})
		    
		WHEN MATCHED THEN
		
			UPDATE SET
				,KWD          = NVL(#{site_keyword} ,KWD)	/* 검색엔진 키워드 */
				,FABICON_NM   = NVL(#{fabicon_nm} ,FABICON_NM)	/* 브라우저 타이틀설정 */
				,FABICON_IMG  = NVL(#{favicon_img} ,FABICON_IMG)	/*파비콘설정*/
				,REGR_NO 	  = #{regr_no}	/* 작성자 */
				,UPD_DTS      = SYSDATE	/* 작성일 */

		WHEN NOT MATCHED THEN

			INSERT  (
				SITE_NO	/* 사이트번호 */
				, SITE_NM	/* 사이트 이름*/
				, KWD	/* 검색엔진 키워드 */
				, FABICON_NM	/* 브라우저 타이틀설정 */
				, FABICON_IMG	/*파비콘설정*/
				, REGR_NO	/* 작성자 */
				, REG_DTS	/* 작성일 */
				)
		     VALUES (
		     	#{site_no}
		     	, #{site_nm}
		     	, #{site_description}
		     	, #{site_keyword}
		     	, #{fabicon_nm}
		     	, #{favicon_img}
     	 	  	, #{regr_no}
		     	, SYSDATE
		     	)

mapper.select:
SELECT * FROM TBL_ME_GRD 
 WHERE 1=1
  
 AND MEM_GRD_NO != #{grd_no} 
 
 
 AND GRD_NM = #{grd_nm}

mapper.update:
UPDATE TBL_ME_GRD
   SET GRD_NM	= #{grd_nm} 
      ,DC_AMT   = REPLACE(#{dc_amt}, ',' ,'')
      ,PNT_RSV_RT =REPLACE(#{pnt_rsv_rt}, ',' ,'')
      ,UPDR_NO = #{regr_no}
      ,UPD_DTS = SYSDATE
 WHERE MEM_GRD_NO = #{grd_no}

mapper.select:
SELECT PLCY_NO
      ,SITE_NO
      ,PLCY_NM
      ,PLCY_DESC
      ,PLCY_TXT
  FROM TBL_AD_SITE_POLICY 
 WHERE SITE_NO = #{site_no}
  
   AND PLCY_NO = #{plcy_no}
 
  
   AND PLCY_DESC = #{plcy_desc}
 
 
 ORDER BY REG_DTS ASC

SELECT PLCY_NO
      ,SITE_NO
      ,PLCY_NM
      ,PLCY_DESC
      ,PLCY_TXT
  FROM TBL_AD_SITE_POLICY 
 WHERE SITE_NO = #{site_no}
  
   AND PLCY_NO = #{plcy_no}
 
  
   AND PLCY_DESC = #{plcy_desc}

mapper.insert:
INSERT INTO TBL_AD_SITE_POLICY (PLCY_NO , SITE_NO, PLCY_NM,PLCY_DESC
                                ,PLCY_TXT,REGR_NO,REG_DTS)
     VALUES (SEQ_AD_SITE_POLICY.NEXTVAL
            ,#{site_no}
            ,#{plcy_nm}
            ,#{plcy_desc}
            ,#{plcy_txt}
            ,#{regr_no}
            , SYSDATE)

mapper.update:
UPDATE TBL_AD_SITE_POLICY 
   SET PLCY_NM = #{plcy_nm}
     , PLCY_DESC  = #{plcy_desc}
     , PLCY_TXT   = #{plcy_txt}
     , UPDR_NO    = #{regr_no}
     , UPD_DTS    = SYSDATE
 WHERE PLCY_NO = #{plcy_no}
